<?php defined('DOCROOT')or die('Access Denied');/*** @Author: wonli <wonli@live.com>*/class AdminView extends BaseView{	function login($data)    {        $this->set(array(            'layer' => 'login'        ));        if(! empty( $data ['notes'] ) )        {            $this->js_notes($data['notes']);        }                $vcode_location = $data["location"];                include $this->tpl('admin/login');    }        function welcome()    {        include $this->tpl("admin/welcome");    }    }